mapper.update:
update user_tbl set
pro_filename=#{proFilename}, pro_filepath=#{proFilepath}
	where user_id=#{userId}

mapper.select:
select a.* from ( select apply_idx as applyIdx, guest_name as guestName, start_addr1 as startAddr1, start_addr2 as startAddr2, start_addr3 as startAddr3, start_latitude as startLatitude, start_longitude as startLongitude, end_addr1 as endAddr1, end_addr2 as endAddr2, end_addr3 as endAddr3, end_latitude as endLatitude, start_longitude as endLongitude, apply_date as applyDate, apply_time as applyTime, ROW_NUMBER() OVER(ORDER BY apply_Idx DESC) rNUM from move_apply_tbl where apply_status =0
and guest_name like '%'||#{userName}||'%'
and apply_date between to_date(replace('${minDate}','-','')||'000000', 'YYYYMMDDhh24miss') and to_date(replace('${maxDate}','-','')||'235959','YYYYMMDDhh24miss')
) a where rNUM between (${page}-1)*${listSize}+1 and ${page}*${listSize}

select a.* from ( select apply_idx as applyIdx, apply_date as applyDate, apply_time as applyTime, guest_name as guestName, guest_phone as guestPhone, start_addr1 as startAddr1, end_addr1 as endAddr1, apply_hope as applyHope, apply_status as applyStatus, ROW_NUMBER() OVER(ORDER BY apply_Idx DESC) rNUM from move_apply_tbl where (apply_status =1 or apply_status =2 or apply_status =3)
and guest_name like '%'||#{userName}||'%'
and apply_date between to_date(replace(#{minDate},'-','')||'000000', 'YYYYMMDDhh24miss') and to_date(replace(#{maxDate},'-','')||'235959','YYYYMMDDhh24miss')
) a where rNUM between (#{page}-1)*#{listSize}+1 and #{page}*#{listSize}
