select user_idx as userIdx, user_id as userId, user_pw as userPw, user_name as userName, addr as userAddr, addr_code as addrCode, user_phone as userPhone, user_email as userEmail, pro_filename as proFilename, pro_filepath as proFilepath, user_introduce as userIntroduce, registration_number as registrationNumber, business_number as businessNumber, user_car as userCar, comp_filename as compFilename, comp_filepath as compFilepath, user_type as userType, social_id as socialId, enroll_date as enrollDate, status, out_date as outDate from user_tbl where
user_id like #{searchWord} and user_Type=#{userType} and status=#{status}
user_name like '%' || #{searchWord} || '%' and user_Type=#{userType} and status=#{status}
user_id like #{searchWord} and user_Type=#{userType} and status=#{status}
user_name like '%' || #{searchWord} || '%' and user_Type=#{userType} and status=#{status}
user_id like '%' || #{searchWord} || '%' and user_Type=#{userType} and status=#{status}
user_name like '%' || #{searchWord} || '%' and user_Type=#{userType} and status=#{status}
user_id like #{searchWord} and status=#{status}
user_name like '%' || #{searchWord} || '%' and (user_type=1 or user_type=2) and status=#{status}

select count(*) cnt	
