mapper.select:
select a.* from (select saleinlots_idx as saleInLotsIDX, saleinlots_state as saleInLotsState, saleinlots_build_type as saleInLotsBuildType, saleinlots_supply_type as saleInLotsSupplyType, saleinlots_name as saleInLotsName, saleinlots_detail_area as saleInLotsDetailArea, saleinlots_price_min_all as saleInLotsPriceMinAll, saleinlots_price_max_all as saleInLotsPriceMaxAll, saleinlots_sale_household as saleInLotsSaleHousehold, saleinlots_min_floor as saleInLotsMinFloor, saleinlots_max_floor as saleInLotsMaxFloor, saleinLots_min_supply_area as saleInLotsMinSupplyArea, saleinLots_max_supply_area as saleInLotsMaxSupplyArea, ROW_NUMBER() OVER(ORDER BY saleinlots_idx ASC) rNUM from saleinlots_tbl
saleinlots_area = #{saleInLotsArea}
AND
saleinlots_build_type like '%'||#{item}||'%'
AND
saleinlots_state like '%'||#{item}||'%'
AND
saleinlots_supply_type like '%'||#{item}||'%'
) a where rNUM between #{start} and #{end}
