mapper.insert:
insert into shop_item_img_tbl ( shop_img_idx, shop_item_idx, shop_img_name, shop_img_path ) select shop_item_img_idx_seq.nextval, shop_item_idx_seq.currval, T.* from(
select #{item.shopImgName} as shop_img_name, #{item.shopImgPath} as shop_img_path from dual
) T

mapper.update:
update shop_item_img_tbl set
	shop_img_name=#{shopImgName},
	shop_img_path=#{shopImgPath}
where shop_item_img_idx=#{shopImgIdx} and shop_item_idx=#{shopItemIdx}
